INSERT INTO vtiger_emailtemplates(foldername, templatename, templatepath, subject, description, body, deleted, systemtemplate, module) VALUES (NULL, 'Event Assigned Notification', NULL, 'Event Assigned Notification', 'Notification for assigned user', '\n<p>Kính gửi $user_name$,<br />\n<br />\nBạn vừa được gán phụ trách một hoạt động.<br />\nThông tin chi tiết</p>\n\n<table cellpadding=\"0\" cellspacing=\"0\" style=\"width:100%;border-collapse:collapse;\">\n	<tbody>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-weight:100;\">Chủ đề</th>\n			<td style=\"padding:3px;border:1px solid #ccc;\">$events-subject$</td>\n		</tr>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-weight:100;\">Thời gian bắt đầu</th>\n			<td style=\"padding:3px;border:1px solid #ccc;\">$date_start$</td>\n		</tr>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-weight:100;\">Thời gian kết thúc</th>\n			<td style=\"padding:3px;border:1px solid #ccc;\">$due_date$</td>\n		</tr>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-weight:100;\">Trạng thái</th>\n			<td style=\"padding:3px;border:1px solid #ccc;\">$events-eventstatus$</td>\n		</tr>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-weight:100;\">Mức độ ưu tiên</th>\n			<td style=\"padding:3px;border:1px solid #ccc;\">$events-priority$</td>\n		</tr>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-weight:100;\">Liên quan đến</th>\n			<td style=\"padding:3px;border:1px solid #ccc;\">$events-crmid$</td>\n		</tr>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-weight:100;\">Danh sách liên hệ</th>\n			<td style=\"padding:3px;border:1px solid #ccc;\">$events-contactid$</td>\n		</tr>\n		<tr>\n			<th style=\"width:20%;text-align:left;padding:3px;border:1px solid #ccc;font-